function topButtonFunction(){60<document.body.scrollTop||60<document.documentElement.scrollTop?$("#top-button").addClass("active"):$("#top-button").removeClass("active")}function displayMenu(){var e=document.getElementById("menu");"hidden"===e.className?(e.classList.remove("hidden"),e.classList.add("show")):(e.classList.remove("show"),e.classList.add("hidden"))}function displayHamburger(){var e=window.innerWidth,t=document.getElementById("menu");767<e?t.classList.remove("hidden","show"):t.classList.add("hidden")}function changeMenuClass(){document.getElementById("burger-nav").onclick=displayMenu}$("#contact-button").click(function(){$("html, body").animate({scrollTop:$("#contact").offset().top},1e3)}),$("#learn-more-button").click(function(){$("html, body").animate({scrollTop:$("#skills").offset().top},1e3)}),$("a:not(#burger-nav)").click(function(){return $("html, body").animate({scrollTop:$($(this).attr("href")).offset().top},1e3),!1}),setInterval(function(){$(".learn-more").toggleClass("toggle",1e3)},2e3),window.onload=function(){changeMenuClass(),displayHamburger()},window.onresize=displayHamburger,window.onscroll=topButtonFunction;var typed=new Typed("#typed",{strings:["Hi, my name is Piotr and I am a junior front-end developer!"],typeSpeed:70,startDelay:800});